    public static boolean existePunto(String cadena){
            boolean resultado;
            resultado = false;

            for(int i = 0; i < cadena.length(); i++){
                if(cadena.substring(i, i+1).equals(".")){
                    resultado = true;
                    break;
                }
            }
        return resultado;
    }
    
    private void jButton14ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton14ActionPerformed
        String cadena;   //Creamos una variable cadena para guardar todo el contenido de la pantalla
        cadena = txtPantalla.getText();
        
        //Corroboramos que la pantalla no este vacia
        if(cadena.length() <= 0){
            txtPantalla.setText("0."); //Si esta vacia le agrego un cero y un punto
        } else {
            if(!existePunto(txtPantalla.getText())){  //Si no esta vacia entro a este bucle
                txtPantalla.setText(txtPantalla.getText()+".");  
            }
        }
    }//GEN-LAST:event_jButton14ActionPerformed

    private void jButton20ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton20ActionPerformed
        String cadena;
        cadena = txtPantalla.getText(); //Tomamos lo que tenemos en pantalla.
        
        if(cadena.length() > 0){ //Si la cadena tiene caracteres, convertimos la cadena quitandole el ultimo elemento.
            cadena = cadena.substring(0, cadena.length() - 1);
            txtPantalla.setText(cadena);
        }
    }//GEN-LAST:event_jButton20ActionPerformed

